import React, { useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, MainContent, ProfileHeading, FormGroup, Label, Select } from '../styles/Components';
import Header from '../components/Header';
import Navigation from '../components/Navigation';
import { useAppContext } from '../contexts/AppContext';
import useTelegram from '../hooks/useTelegram';

const RestaurantSelectionPage: React.FC = () => {
  const navigate = useNavigate();
  const { 
    restaurants, 
    selectedCity, 
    selectedRestaurant, 
    selectRestaurant 
  } = useAppContext();
  
  const { 
    showBackButton, 
    hideBackButton, 
    setBackButtonCallback, 
    showMainButton, 
    setMainButtonCallback 
  } = useTelegram();

  const filteredRestaurants = useMemo(() => {
    if (!selectedCity) return [];
    return restaurants.filter(restaurant => restaurant.cityId === selectedCity.id);
  }, [restaurants, selectedCity]);

  useEffect(() => {
    if (!selectedCity) {
      navigate('/city-selection');
    }
  }, [selectedCity, navigate]);

  useEffect(() => {
    showBackButton();
    showMainButton('Continue');
    
    setBackButtonCallback(() => {
      navigate('/city-selection');
    });
    
    setMainButtonCallback(() => {
      if (selectedRestaurant) {
        navigate('/');
      }
    });

    return () => {
      hideBackButton();
    };
  }, [hideBackButton, navigate, selectedRestaurant, setBackButtonCallback, setMainButtonCallback, showBackButton, showMainButton]);

  const handleRestaurantChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const restaurantId = parseInt(e.target.value, 10);
    const restaurant = restaurants.find(r => r.id === restaurantId);
    if (restaurant) {
      selectRestaurant(restaurant);
    }
  };

  return (
    <Container>
      <Header />
      <Navigation />
      <MainContent>
        <ProfileHeading>Select a restaurant</ProfileHeading>
        <FormGroup>
          <Label htmlFor="restaurant">Restaurant</Label>
          <Select 
            id="restaurant" 
            value={selectedRestaurant?.id || ''} 
            onChange={handleRestaurantChange}
          >
            <option value="">Select a restaurant</option>
            {filteredRestaurants.map(restaurant => (
              <option key={restaurant.id} value={restaurant.id}>
                {restaurant.name}
              </option>
            ))}
          </Select>
        </FormGroup>
      </MainContent>
    </Container>
  );
};

export default RestaurantSelectionPage;
