import React from 'react';
import { FoodCard, FoodImage, FoodInfo, FoodName, FoodPrice, AddToCartButton } from '../styles/Components';
import { useAppContext } from '../contexts/AppContext';
import { FoodItem as FoodItemType } from '../contexts/AppContext';

interface FoodItemProps {
  item: FoodItemType;
}

const FoodItem: React.FC<FoodItemProps> = ({ item }) => {
  const { addToCart } = useAppContext();

  const handleAddToCart = () => {
    addToCart(item);
  };

  return (
    <FoodCard>
      <FoodImage src={item.image} alt={item.name} />
      <FoodInfo>
        <FoodName>{item.name}</FoodName>
        <FoodPrice>â‚½{item.price}</FoodPrice>
        <AddToCartButton onClick={handleAddToCart}>
          Add to Cart
        </AddToCartButton>
      </FoodInfo>
    </FoodCard>
  );
};

export default FoodItem;
