import React from 'react';
import { FoodCard, FoodImage, FoodInfo, FoodName, FoodPrice, AddToCartButton } from '../styles/Components';
import { useAppContext } from '../contexts/AppContext';
import { FoodItem as FoodItemType } from '../contexts/AppContext';
import styled from 'styled-components';

interface FoodItemProps {
  item: FoodItemType;
}

const FoodInfoWrapper = styled(FoodInfo)`
  padding: 16px;
`;

const FoodHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 12px;
`;

const StyledFoodName = styled(FoodName)`
  flex: 1;
  margin-right: 10px;
  margin-bottom: 4px;
`;

const StyledFoodPrice = styled(FoodPrice)`
  font-weight: bold;
  text-align: right;
  white-space: nowrap;
`;

const Description = styled.p`
  font-size: 14px;
  color: #AAA;
  margin-bottom: 15px;
  line-height: 1.4;
`;

const StyledAddButton = styled(AddToCartButton)`
  margin-top: 8px;
  background-color: var(--primary-color);
  color: white;
  padding: 10px;
  border-radius: 8px;
  font-weight: bold;
  transition: all 0.2s ease;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  }
`;

const FoodItem: React.FC<FoodItemProps> = ({ item }) => {
  const { addToCart } = useAppContext();

  const handleAddToCart = () => {
    addToCart(item);
  };

  return (
    <FoodCard>
      <FoodImage src={item.image} alt={item.name} />
      <FoodInfoWrapper>
        <FoodHeader>
          <StyledFoodName>{item.name}</StyledFoodName>
          <StyledFoodPrice>â‚½{item.price}</StyledFoodPrice>
        </FoodHeader>
        
        {item.description && (
          <Description>{item.description}</Description>
        )}
        
        <StyledAddButton onClick={handleAddToCart}>
          Add to Cart
        </StyledAddButton>
      </FoodInfoWrapper>
    </FoodCard>
  );
};

export default FoodItem;
