import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Container, 
  MainContent, 
  ProfileHeading, 
  FoodCard, 
  FoodInfo, 
  FoodName, 
  FoodPrice, 
  AddToCartButton, 
  OptionButtons, 
  OptionButton,
  FormGroup,
  Label,
  Input
} from '../styles/Components';
import Header from '../components/Header';
import Navigation from '../components/Navigation';
import { useAppContext } from '../contexts/AppContext';
import useTelegram from '../hooks/useTelegram';

const CartPage: React.FC = () => {
  const navigate = useNavigate();
  const { 
    cart, 
    removeFromCart, 
    updateCartItemQuantity,
    deliveryMethod,
    clearCart,
    userAddress,
    updateUserAddress,
    placeOrder
  } = useAppContext();
  
  const { 
    showBackButton, 
    hideBackButton, 
    setBackButtonCallback, 
    showMainButton, 
    hideMainButton,
    setMainButtonCallback 
  } = useTelegram();

  const totalAmount = cart.reduce(
    (sum, item) => sum + item.price * item.quantity, 
    0
  );

  useEffect(() => {
    showBackButton();
    
    if (cart.length > 0) {
      showMainButton('Place Order');
    } else {
      hideMainButton();
    }
    
    setBackButtonCallback(() => {
      navigate('/');
    });
    
    setMainButtonCallback(() => {
      if (cart.length > 0) {
        placeOrder();
        navigate('/profile');
      }
    });

    return () => {
      hideBackButton();
      hideMainButton();
    };
  }, [
    cart, 
    hideBackButton, 
    hideMainButton, 
    navigate, 
    placeOrder, 
    setBackButtonCallback, 
    setMainButtonCallback, 
    showBackButton, 
    showMainButton
  ]);

  const handleQuantityChange = (itemId: number, newQuantity: number) => {
    if (newQuantity <= 0) {
      removeFromCart(itemId);
    } else {
      updateCartItemQuantity(itemId, newQuantity);
    }
  };

  const handleRemoveItem = (itemId: number) => {
    removeFromCart(itemId);
  };

  const handleClearCart = () => {
    clearCart();
  };

  const handleAddressChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    updateUserAddress({
      ...userAddress || {
        street: '',
        houseNumber: '',
      },
      [name]: value
    });
  };

  return (
    <Container>
      <Header />
      <Navigation />
      <MainContent>
        <ProfileHeading>Your Cart</ProfileHeading>
        
        {cart.length === 0 ? (
          <p>Your cart is empty</p>
        ) : (
          <>
            {cart.map(item => (
              <FoodCard key={item.id} style={{ marginBottom: '16px' }}>
                <FoodInfo>
                  <FoodName>{item.name}</FoodName>
                  <FoodPrice>₽{item.price} x {item.quantity} = ₽{item.price * item.quantity}</FoodPrice>
                  
                  <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                    <AddToCartButton 
                      onClick={() => handleQuantityChange(item.id, item.quantity - 1)}
                    >
                      -
                    </AddToCartButton>
                    <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', width: '40px' }}>
                      {item.quantity}
                    </span>
                    <AddToCartButton 
                      onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
                    >
                      +
                    </AddToCartButton>
                    <AddToCartButton 
                      onClick={() => handleRemoveItem(item.id)}
                      style={{ marginLeft: 'auto' }}
                    >
                      Remove
                    </AddToCartButton>
                  </div>
                </FoodInfo>
              </FoodCard>
            ))}
            
            <div style={{ margin: '20px 0', textAlign: 'right' }}>
              <ProfileHeading>Total: ₽{totalAmount}</ProfileHeading>
              <AddToCartButton onClick={handleClearCart}>
                Clear Cart
              </AddToCartButton>
            </div>

            {deliveryMethod === 'delivery' && (
              <div style={{ marginTop: '20px' }}>
                <ProfileHeading>Delivery Address</ProfileHeading>
                <FormGroup>
                  <Label htmlFor="street">Street</Label>
                  <Input
                    type="text"
                    id="street"
                    name="street"
                    value={userAddress?.street || ''}
                    onChange={handleAddressChange}
                    placeholder="Enter street name"
                  />
                </FormGroup>
                <FormGroup>
                  <Label htmlFor="houseNumber">House Number</Label>
                  <Input
                    type="text"
                    id="houseNumber"
                    name="houseNumber"
                    value={userAddress?.houseNumber || ''}
                    onChange={handleAddressChange}
                    placeholder="Enter house number"
                  />
                </FormGroup>
                <FormGroup>
                  <Label htmlFor="apartment">Apartment (optional)</Label>
                  <Input
                    type="text"
                    id="apartment"
                    name="apartment"
                    value={userAddress?.apartment || ''}
                    onChange={handleAddressChange}
                    placeholder="Enter apartment number"
                  />
                </FormGroup>
                <FormGroup>
                  <Label htmlFor="floor">Floor (optional)</Label>
                  <Input
                    type="text"
                    id="floor"
                    name="floor"
                    value={userAddress?.floor || ''}
                    onChange={handleAddressChange}
                    placeholder="Enter floor number"
                  />
                </FormGroup>
                <FormGroup>
                  <Label htmlFor="comment">Delivery Comments (optional)</Label>
                  <Input
                    type="text"
                    id="comment"
                    name="comment"
                    value={userAddress?.comment || ''}
                    onChange={handleAddressChange}
                    placeholder="Any special instructions for delivery"
                  />
                </FormGroup>
              </div>
            )}
          </>
        )}
      </MainContent>
    </Container>
  );
};

export default CartPage;
