import React, { useEffect } from 'react';
import { Container, MainContent, FoodGrid, ProfileHeading } from '../styles/Components';
import Header from '../components/Header';
import Navigation from '../components/Navigation';
import FoodItem from '../components/FoodItem';
import CartButton from '../components/CartButton';
import { useAppContext } from '../contexts/AppContext';
import { useNavigate } from 'react-router-dom';
import useTelegram from '../hooks/useTelegram';

const HomePage: React.FC = () => {
  const { 
    foodItems, 
    selectedCity, 
    selectedRestaurant 
  } = useAppContext();
  const navigate = useNavigate();
  const { hideMainButton } = useTelegram();

  useEffect(() => {
    if (!selectedCity) {
      navigate('/city-selection');
    } else if (!selectedRestaurant) {
      navigate('/restaurant-selection');
    }
  }, [selectedCity, selectedRestaurant, navigate]);

  useEffect(() => {
    hideMainButton();
  }, [hideMainButton]);

  return (
    <Container>
      <Header />
      <Navigation />
      <MainContent>
        {selectedRestaurant ? (
          <>
            <ProfileHeading>{selectedRestaurant.name} Menu</ProfileHeading>
            <FoodGrid>
              {foodItems.map(item => (
                <FoodItem key={item.id} item={item} />
              ))}
            </FoodGrid>
          </>
        ) : (
          <ProfileHeading>Please select a restaurant to see the menu</ProfileHeading>
        )}
      </MainContent>
      <CartButton />
    </Container>
  );
};

export default HomePage;
